package main

import (
	"fmt"
	"strconv"
)

func digital_root(n int) int {

	// Convert n to a string
	s := fmt.Sprint(n)

	// Loop through every char in the string
	total := 0
	for i := 0; i < len(s); i++ {
		x, err := strconv.Atoi(string(s[i])) // Convert current char to an int

		// If there was an error converting the char, return it as-is
		if err!= nil {
			return x
		}

		// Add up all digits in the string
		total += x
	}

	// Recursively call this function with the total until we get single-digit result
	return digital_root(total)
}

func main() {
	numbers := []int{4937, 9856, 2363, 8754, 5689}

	for _, num := range numbers {
		result := digital_root(num)
		fmt.Println("Digital root of", num, "is", result)
	}
}